$(document).ready(function(){$("#billing-address-js").on("click",function(e){e.preventDefault();window.location.href=billingAddressUrl});$('#shipping-address-js input[name="address_id"]').on("change",function(){const $layer=$("#shipping-address-js");const $this=$(this);if($layer.prop("disabled")){return}const address_id=$this.val();if(!address_id){return}$layer.prop("disabled",true);$this.closest(".address-item-js").find(".address-item-name-js").append(' <span class="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>');$.ajax({url:setDefaultUrl,type:"post",data:{address_id:address_id,[csrfName]:csrfVal},dataType:"json",success:function(response){if(response.status=="success"){location.reload()}else{showToast(translations.systemError,"#toast-js");$layer.prop("disabled",false);$this.closest(".address-item-js").find(".address-item-name-js").find(".spinner-border").remove()}},error:function(XMLHttpRequest,textStatus,errorThrown){showToast(translations.systemError,"#toast-js");$layer.prop("disabled",false);$this.closest(".address-item-js").find(".address-item-name-js").find(".spinner-border").remove()}})})});